import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Link, useNavigate } from 'react-router-dom';
import { logout } from '../store/authSlice';
import { ROLES } from '../config/roles';

const Header = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { user, role: userRole } = useSelector(state => state.auth);

  const handleLogout = () => {
    dispatch(logout());
    navigate('/');
  };

  const renderNavLinks = () => {
    const navLinks = [
      { name: 'Dashboard', path: '/dashboard', roles: [ROLES.ADMIN, ROLES.MANAGER, ROLES.EMPLOYEE] },
      { name: 'Tasks', path: '/tasks', roles: [ROLES.ADMIN, ROLES.MANAGER, ROLES.EMPLOYEE] },
      { name: 'Goals', path: '/goals', roles: [ROLES.ADMIN, ROLES.MANAGER] },
    ];

    // Add admin-specific links
    if (userRole === ROLES.ADMIN) {
      navLinks.push(
        { name: 'Role Management', path: '/admin/roles', roles: [ROLES.ADMIN] }
      );
    }

    return navLinks
      .filter(link => link.roles.includes(userRole))
      .map(link => (
        <Link 
          key={link.path} 
          to={link.path} 
          className="text-gray-300 hover:bg-gray-700 hover:text-white px-3 py-2 rounded-md text-sm font-medium"
        >
          {link.name}
        </Link>
      ));
  };

  return (
    <header className="bg-gray-800 text-white p-4 flex justify-between items-center">
      <div className="flex items-center">
        <h1 className="text-xl font-bold mr-6">Budget Tracker</h1>
        <nav className="flex space-x-4">
          {renderNavLinks()}
        </nav>
      </div>
      
      <div className="flex items-center">
        {user && (
          <div className="mr-4">
            <span className="text-sm">
              {user.email} 
              <span className="ml-2 px-2 py-1 bg-blue-600 text-white rounded-full text-xs uppercase">
                {userRole}
              </span>
            </span>
          </div>
        )}
        <button 
          onClick={handleLogout}
          className="bg-red-600 hover:bg-red-700 text-white px-3 py-2 rounded-md text-sm"
        >
          Logout
        </button>
      </div>
    </header>
  );
};

export default Header;
