import React from 'react';

const LoginButton = ({ className }) => {
  const handleLogin = () => {
    console.log('Login button clicked');
    const baseUrl = process.env.NODE_ENV === 'production'
      ? 'https://vb-budgettracker.netlify.app'
      : 'http://localhost:5000';

    console.log('Redirecting to Google OAuth:', `${baseUrl}/auth/google`);

    try {
      window.location.href = `${baseUrl}/auth/google`;
    } catch (error) {
      console.error('Error redirecting to Google OAuth:', error);
      alert('An error occurred while redirecting to Google authentication. Please check your browser settings and ensure JavaScript is enabled.');
    }
  };

  return (
    <div className="flex justify-center items-center h-[80vh]">
      <button
        onClick={handleLogin}
        className={className || `
          flex items-center gap-4 
          bg-white text-gray-800 
          font-semibold 
          py-4 px-8 
          border border-gray-400 
          rounded-lg shadow-lg 
          hover:bg-gray-100 
          text-xl 
          transform transition-transform 
          hover:scale-105 
        `}
      >
        <img 
          src="https://www.gstatic.com/firebasejs/ui/2.0.0/images/auth/google.svg" 
          alt="Google Logo" 
          className="w-8 h-8"
        />
        Sign in with Google
      </button>
      <noscript>
        <p className="text-red-600 mt-4">
          JavaScript is required to sign in with Google. Please enable JavaScript in your browser settings.
        </p>
      </noscript>
    </div>
  );
};

export default LoginButton;
