import React from 'react';
import { Navigate, Outlet } from 'react-router-dom';
import { ROLES } from '../config/roles';
import { toast } from 'react-hot-toast';

const ProtectedRoute = ({ 
  children, 
  allowedRoles = [], 
  isAuthenticated = false, 
  userRole 
}) => {
  console.log('ProtectedRoute - isAuthenticated:', isAuthenticated);
  console.log('ProtectedRoute - role:', userRole);
  console.log('ProtectedRoute - allowedRoles:', allowedRoles);

  // If not authenticated, redirect to login
  if (!isAuthenticated) {
    toast.error('Please log in to access this page');
    return <Navigate to="/" replace />;
  }

  // If role is undefined, show a more informative error
  if (userRole === undefined) {
    toast.error('User role could not be determined. Please log out and log in again.');
    return (
      <div className="flex justify-center items-center h-full text-center text-red-600 p-6">
        <div>
          <h2 className="text-2xl font-bold mb-4">Role Determination Error</h2>
          <p>Your user role could not be determined.</p>
          <p>Please log out and log in again.</p>
        </div>
      </div>
    );
  }

  // Normalize roles to ensure consistent comparison
  const normalizedUserRole = userRole?.toLowerCase();
  const normalizedAllowedRoles = allowedRoles.map(role => role.toLowerCase());

  // Check if user's role is allowed
  const hasRequiredRole = normalizedAllowedRoles.length === 0 || 
    normalizedAllowedRoles.includes(normalizedUserRole);

  // If user doesn't have required role, show access denied
  if (!hasRequiredRole) {
    toast.error('Insufficient permissions to access this page');
    return (
      <div className="flex justify-center items-center h-full text-center text-red-600 p-6">
        <div>
          <h2 className="text-2xl font-bold mb-4">Access Denied</h2>
          <p>You do not have permission to access this page.</p>
          <p>Required Role: {allowedRoles.join(' or ')}</p>
          <p>Your Current Role: {userRole}</p>
        </div>
      </div>
    );
  }

  // If children are provided, render them
  if (children) {
    return children;
  }

  // Otherwise, render child routes
  return <Outlet />;
};

export default ProtectedRoute;
