import React from 'react';

const LoginButton = () => {
  const handleLogin = () => {
    // Redirect to the backend's Google OAuth endpoint
    window.location.href = 'http://localhost:5000/auth/google';
  };

  return (
    <button
      onClick={handleLogin}
      style={{
        backgroundColor: '#4285F4',
        color: 'white',
        padding: '8px 16px',
        borderRadius: '4px',
        border: 'none',
        cursor: 'pointer',
      }}
    >
      Login with Google
    </button>
  );
};

export default LoginButton;
