import React from 'react';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { logout } from '../store/authSlice';

const Header = () => {
  const dispatch = useDispatch();
  const { user, role: userRole, isAuthenticated } = useSelector(state => state.auth);

  console.log('User Role in Header:', userRole, 'Authenticated:', isAuthenticated);

  const navigation = [
    { name: 'Dashboard', href: '/', roles: ['admin', 'manager', 'employee'] },
    { name: 'Tasks', href: '/tasks', roles: ['admin', 'manager', 'employee'] },
    { name: 'Goals', href: '/goals', roles: ['admin', 'manager'] },
    { name: 'Goal Input', href: '/goals/input', roles: ['admin', 'manager'] },
    { name: 'Role Management', href: '/admin', roles: ['admin'] }
  ];

  const filteredNavigation = isAuthenticated ? navigation.filter(item => 
    userRole && item.roles.includes(userRole)
  ) : [];

  const handleLogout = () => {
    dispatch(logout());
  };

  return (
    <header>
      <nav className="bg-gray-800">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <span className="text-white font-bold">Budget Tracker</span>
              </div>
              <div className="hidden md:block">
                <div className="ml-10 flex items-baseline space-x-4">
                  {filteredNavigation.map((item, index) => (
                    <Link
                      key={index}
                      to={item.href}
                      className="text-gray-300 hover:bg-gray-700 hover:text-white px-3 py-2 rounded-md text-sm font-medium"
                    >
                      {item.name}
                    </Link>
                  ))}
                </div>
              </div>
            </div>
            <div className="hidden md:block">
              <div className="ml-4 flex items-center md:ml-6">
                <button 
                  className="bg-gray-800 p-1 rounded-full text-gray-400 hover:text-white focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-800 focus:ring-white"
                  onClick={handleLogout}
                >
                  Logout
                </button>
              </div>
            </div>
          </div>
        </div>
      </nav>
    </header>
  );
};

export default Header;
