import React from 'react';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { logout } from '../store/authSlice';

const Header = () => {
  const dispatch = useDispatch();
  const userRole = useSelector(state => state.auth.role);

  console.log('User Role in Header:', userRole);

  const navigation = [
    { name: 'Dashboard', href: '/', roles: ['admin', 'manager', 'employee'] },
    { name: 'Tasks', href: '/tasks', roles: ['admin', 'manager', 'employee'] },
    { name: 'Goals', href: '/goals', roles: ['admin', 'manager'] },
    { name: 'Goal Input', href: '/goals/input', roles: ['admin', 'manager'] },
    { name: 'Role Management', href: '/admin', roles: ['admin'] }
  ];

  const handleLogout = () => {
    dispatch(logout());
  };

  const filteredNavigation = navigation.filter(item => 
    item.roles.includes(userRole)
  );

  return (
    <nav className="bg-gray-800">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <span className="text-white font-bold">Budget Tracker</span>
            </div>
            <div className="hidden md:block">
              <div className="ml-10 flex items-baseline space-x-4">
                {filteredNavigation.map((item) => (
                  <Link
                    key={item.name}
                    to={item.href}
                    className="text-gray-300 hover:bg-gray-700 hover:text-white px-3 py-2 rounded-md text-sm font-medium"
                  >
                    {item.name}
                  </Link>
                ))}
              </div>
            </div>
          </div>
          <div>
            <button
              onClick={handleLogout}
              className="text-gray-300 hover:bg-gray-700 hover:text-white px-3 py-2 rounded-md text-sm font-medium"
            >
              Logout
            </button>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Header;
