import React, { useState, useEffect } from 'react';
import axios from 'axios';

const GoalInput = () => {
  const [goals, setGoals] = useState([]);
  const [newGoal, setNewGoal] = useState({
    title: '',
    description: '',
    targetDate: '',
    targetAmount: '',
    status: 'pending'
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch goals on component mount
  useEffect(() => {
    fetchGoals();
  }, []);

  const fetchGoals = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/goals', {
        withCredentials: true
      });
      setGoals(response.data);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching goals:', err);
      setError('Failed to fetch goals. Please try again later.');
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewGoal(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/goals', newGoal, {
        withCredentials: true
      });
      // Reset form and refresh goals list
      setNewGoal({
        title: '',
        description: '',
        targetDate: '',
        targetAmount: '',
        status: 'pending'
      });
      fetchGoals();
    } catch (err) {
      console.error('Error creating goal:', err);
      setError('Failed to create goal. Please try again.');
    }
  };

  if (loading) {
    return <div className="text-center p-4">Loading...</div>;
  }

  if (error) {
    return <div className="text-red-600 text-center p-4">{error}</div>;
  }

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-2xl font-bold mb-6">Goal Input</h1>
      
      {/* Goal Input Form */}
      <form onSubmit={handleSubmit} className="mb-8 bg-white shadow-md rounded px-8 pt-6 pb-8">
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="title">
            Title
          </label>
          <input
            type="text"
            id="title"
            name="title"
            value={newGoal.title}
            onChange={handleInputChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="description">
            Description
          </label>
          <textarea
            id="description"
            name="description"
            value={newGoal.description}
            onChange={handleInputChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            rows="3"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="targetDate">
            Target Date
          </label>
          <input
            type="date"
            id="targetDate"
            name="targetDate"
            value={newGoal.targetDate}
            onChange={handleInputChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="targetAmount">
            Target Amount
          </label>
          <input
            type="number"
            id="targetAmount"
            name="targetAmount"
            value={newGoal.targetAmount}
            onChange={handleInputChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            required
          />
        </div>

        <div className="flex items-center justify-between">
          <button
            type="submit"
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          >
            Add Goal
          </button>
        </div>
      </form>

      {/* Goals Table */}
      <div className="bg-white shadow-md rounded my-6">
        <table className="min-w-full table-auto">
          <thead>
            <tr className="bg-gray-200 text-gray-600 uppercase text-sm leading-normal">
              <th className="py-3 px-6 text-left">Title</th>
              <th className="py-3 px-6 text-left">Description</th>
              <th className="py-3 px-6 text-center">Target Date</th>
              <th className="py-3 px-6 text-center">Target Amount</th>
              <th className="py-3 px-6 text-center">Status</th>
            </tr>
          </thead>
          <tbody className="text-gray-600 text-sm font-light">
            {goals.map((goal) => (
              <tr key={goal.id} className="border-b border-gray-200 hover:bg-gray-100">
                <td className="py-3 px-6 text-left whitespace-nowrap">
                  <div className="font-medium">{goal.title}</div>
                </td>
                <td className="py-3 px-6 text-left">
                  <div className="font-medium">{goal.description}</div>
                </td>
                <td className="py-3 px-6 text-center">
                  {new Date(goal.targetDate).toLocaleDateString()}
                </td>
                <td className="py-3 px-6 text-center">
                  ${goal.targetAmount}
                </td>
                <td className="py-3 px-6 text-center">
                  <span className={`bg-${goal.status === 'completed' ? 'green' : 'yellow'}-200 text-${goal.status === 'completed' ? 'green' : 'yellow'}-600 py-1 px-3 rounded-full text-xs`}>
                    {goal.status}
                  </span>
                </td>
              </tr>
            ))}
            {goals.length === 0 && (
              <tr>
                <td colSpan="5" className="text-center py-4">
                  No goals found. Add your first goal using the form above.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default GoalInput;
