import React from 'react';

const LoginButton = ({ className }) => {
  const handleLogin = () => {
    window.location.href = 'http://localhost:5000/auth/google';
  };

  return (
    <div className="flex justify-center items-center h-screen">
      <button
        onClick={handleLogin}
        className={className || "flex items-center gap-4 bg-white text-gray-800 font-semibold py-3 px-6 border border-gray-400 rounded-lg shadow hover:bg-gray-100 text-lg"}
      >
        <img 
          src="https://www.gstatic.com/firebasejs/ui/2.0.0/images/auth/google.svg" 
          alt="Google Logo" 
          className="w-7 h-7"
        />
        Sign in with Google
      </button>
    </div>
  );
};

export default LoginButton;
