import React, { useState, useEffect } from 'react';
import axios from 'axios';

const LoginButton = ({ className }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    checkAuthStatus();
  }, []);

  const checkAuthStatus = async () => {
    try {
      const response = await axios.get('http://localhost:5000/auth/status', { withCredentials: true });
      setIsAuthenticated(response.data.authenticated);
      setUser(response.data.user);
    } catch (error) {
      console.error('Error checking auth status:', error);
      setIsAuthenticated(false);
      setUser(null);
    }
  };

  const handleLogin = () => {
    // Redirect to Google OAuth login
    window.location.href = 'http://localhost:5000/auth/google';
  };

  if (isAuthenticated && user) {
    // Safely render user name
    const userName = user.displayName || 
      (user.name && `${user.name.givenName} ${user.name.familyName}`.trim()) || 
      'User';

    return (
      <div className="flex items-center">
        <img
          src={user.photos?.[0]?.value || user.picture || 'https://via.placeholder.com/32'}
          alt="Profile"
          className="w-8 h-8 rounded-full mr-2"
        />
        <span className="text-sm text-gray-700">{userName}</span>
      </div>
    );
  }

  return (
    <button
      onClick={handleLogin}
      className={className || "flex items-center gap-2 bg-white text-gray-800 font-semibold py-2 px-4 border border-gray-400 rounded shadow hover:bg-gray-100"}
    >
      <img 
        src="https://www.gstatic.com/firebasejs/ui/2.0.0/images/auth/google.svg" 
        alt="Google Logo" 
        className="w-5 h-5"
      />
      Sign in with Google
    </button>
  );
};

export default LoginButton;
